INSERT INTO public.testimonials (author_name, content, rating, status, approved_at) VALUES
('Александр М.', 'Обратился за помощью с вопросом о военной службе. Получил детальную консультацию по моему диагнозу плоскостопия. Помогли собрать все необходимые документы и правильно оформить освидетельствование. Результат - военный билет. Спасибо!', 5, 'approved', NOW()),
('Дмитрий К.', 'Отличная юридическая поддержка! Консультировали на каждом этапе прохождения комиссии. Объяснили все мои права и помогли добиться справедливого решения. Рекомендую!', 5, 'approved', NOW()),
('Михаил П.', 'Профессиональный подход к делу. Помогли разобраться с медицинскими документами и подготовили правильную стратегию взаимодействия с военкоматом. Все прошло успешно.', 5, 'approved', NOW()),
('Иван С.', 'Спасибо за оперативную помощь! В сжатые сроки помогли подготовить документы для обжалования решения призывной комиссии. Результат положительный.', 5, 'approved', NOW()),
('Андрей Л.', 'Квалифицированная юридическая помощь. Консультации были четкими и понятными. Помогли законно решить вопрос с призывом. Очень доволен результатом!', 5, 'approved', NOW());